import useInput from "./use-input";

const Contacto = () => {
  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    hasError: nameInputHasError,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
    reset: resetNameInput,
  } = useInput((value) => value.length >= 3 && value.length < 50);

  const {
    value: enteredLastname,
    isValid: enteredLastnameIsValid,
    hasError: lastnameInputHasError,
    valueChangeHandler: lastnameChangeHandler,
    inputBlurHandler: lastnameBlurHandler,
    reset: resetLastnameInput,
  } = useInput((value) => value.length >= 3 && value.length < 50);

  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: emailInputHasError,
    valueChangeHandler: emailChangeHandler,
    inputBlurHandler: emailBlurHandler,
    reset: resetEmailInput,
  } = useInput((value) => value.includes("@"));

  const {
    value: enteredPhone,
    isValid: enteredPhoneIsValid,
    hasError: phoneInputHasError,
    valueChangeHandler: phoneChangeHandler,
    inputBlurHandler: phoneBlurHandler,
    reset: resetPhoneInput,
  } = useInput((value) => !isNaN(value) && value.length <= 8);

  return (
    <form className="form">
      <h2>Contáctenos</h2>

      <div className="input">
        <label htmlFor="nombre">Nombre</label>
        <input
          type="text"
          id="nombre"
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          value={enteredName}
        />
        {nameInputHasError && <p className="error-text">Máximo 50. Mínimo 3</p>}
      </div>
      <div className="input">
        <label htmlFor="apellidos">Apellidos</label>
        <input
          type="text"
          id="apellidos"
          onChange={lastnameChangeHandler}
          onBlur={lastnameBlurHandler}
          value={enteredLastname}
        />
        {lastnameInputHasError && (
          <p className="error-text">Máximo 50. Mínimo 3</p>
        )}
      </div>
      <div className="input">
        <label htmlFor="correo">Correo</label>
        <input
          type="text"
          id="correo"
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
          value={enteredEmail}
        />
        {emailInputHasError && <p className="error-text">debe incluir @</p>}
      </div>
      <div className="input">
        <label htmlFor="telefono">Teléfono</label>
        <input type="text" id="telefono" />
      </div>
      <div className="asunto">
        <label htmlFor="asunto">Asunto</label>
        <input type="text" id="asunto" />
      </div>

      <div className="input">
        <button>Enviar</button>
      </div>
    </form>
  );
};

export default Contacto;
